import UIKit

@objc protocol MemoDetailRoutingLogic {
    func routeToBackPage()
}

protocol MemoDetailDataPassing {
    var dataStore: MemoDetailDataStore? { get }
}

class MemoDetailRouter: NSObject, MemoDetailRoutingLogic, MemoDetailDataPassing {
    weak var viewController: MemoDetailViewController?
    var dataStore: MemoDetailDataStore?
    
    // MARK: Routing
    
    func routeToBackPage() {
        print("List 페이지로 돌아가기")
        navigateToBackPage(source: viewController!)
    }
    
    // MARK: Navigation
    func navigateToBackPage(source: MemoDetailPage) {
        source.navigationController?.popViewController(animated: true)
    }
}
